package com.thoughtworks.railroad;public class Edge {  private final Vertex destination;  private final int distance;  public Edge() {    destination = null;    distance = 0;  }  public Edge(Vertex n, int d) {    destination = n;    distance = d;  }    public Vertex getDestination() {    return destination;  }  public int getDistance() {    return distance;  }  public String toString() {    return destination.getName() + ": " + distance;  }}