package com.thoughtworks.railroad;import java.util.ArrayList;import java.util.List;public class Vertex {  private List<Edge> neighbours;  private final String name;  public Vertex(String name) {    neighbours = new ArrayList<Edge>();    this.name = name;  }  String getName() {     return name;  }  List<Edge> getNeighbours() {    return neighbours;  }  public void addEdge(Edge edge) {    neighbours.add(edge);  }  /* (non-Javadoc)   * @see java.lang.Object#hashCode()   */  @Override  public int hashCode() {    final int prime = 31;    int result = 1;    result = prime * result + ((name == null) ? 0 : name.hashCode());    return result;  }  /* (non-Javadoc)   * @see java.lang.Object#equals(java.lang.Object)   */  @Override  public boolean equals(Object obj) {    if (this == obj) {      return true;    }    if (obj == null) {      return false;    }    if (!(obj instanceof Vertex)) {      return false;    }    Vertex other = (Vertex) obj;    if (name == null) {      if (other.name != null) {        return false;      }    } else if (!name.equals(other.name)) {      return false;    }    return true;  }}