package com.thoughtworks.railroad.controller;import com.thoughtworks.railroad.exception.NoSuchPathException;import com.thoughtworks.railroad.exception.WrongInputException;import com.thoughtworks.railroad.model.GraphBuilder;import com.thoughtworks.railroad.model.IGraph;import com.thoughtworks.railroad.model.Vertex;public class Controller {  private static IGraph graph;  private Controller() {    if (graph == null) {      graph = GraphBuilder.getDirectedGraph();    }  }  public static Controller getRouter() {    return new Controller();  }    public IGraph getGraph() {    return graph;  }    public String getDistance(String inputPath) {    String nodes[] = inputPath.split("-");        if(nodes.length < 2) {      throw new WrongInputException("Distance: " + inputPath);    }        String from = nodes[0].trim().toUpperCase();    Vertex start = new Vertex(from);        Vertex[] stops = new Vertex[nodes.length - 1];    for(int i = 1; i < nodes.length; ++i) {      String stop = nodes[i].trim().toUpperCase();      stops[i - 1] = new Vertex(stop);    }    String distance = null;        try {      distance = graph.calculateDistance(start, stops);    } catch(NoSuchPathException e) {      return e.getMessage();    }        return distance;  }}