package com.thoughtworks.railroad.model.condition;import com.thoughtworks.railroad.model.Path;/** * Exact filter that required the number of nodes processed is exactly equal to * the limit *  * @author Narasimman *  */public class ExactCondition implements Condition {  private final int max;  public ExactCondition(int m) {    max = m;  }  @Override  public boolean pass(Path path) {    if (path == null) {      return false;    }    return path.getNumOfNodes() == max;  }}