package com.thoughtworks.railroad.model.graph;import java.util.List;import com.thoughtworks.railroad.model.Path;import com.thoughtworks.railroad.model.Vertex;import com.thoughtworks.railroad.model.condition.Condition;/** * This interface *  * @author Narasimman *  */public interface IGraph {  public boolean addVertex(Vertex v);  public boolean addEdge(Vertex from, Vertex to, int weight);  public int getNumberOfVertices();  public String calculateDistance(Vertex from, Vertex[] nodes);  public Path getShortestPath(Vertex from, Vertex to);  public List<Path> getAllPaths(Vertex from, Vertex to,      Condition terminalCondition, Condition maxCondition);}