package com.thoughtworks.railroad.main;import static org.junit.Assert.*;import org.junit.After;import org.junit.AfterClass;import org.junit.Before;import org.junit.BeforeClass;import org.junit.Test;import com.thoughtworks.railroad.exception.WrongInputException;public class TestDriver {  @BeforeClass  public static void setUpBeforeClass() throws Exception {  }  @AfterClass  public static void tearDownAfterClass() throws Exception {  }  @Before  public void setUp() throws Exception {  }  @After  public void tearDown() throws Exception {  }  @Test  public final void testValidInput() {    String actual = Driver.execute("graph.txt", "command1.txt");         assertEquals("Result should be 9", "9\r\n", actual);  }  @Test (expected = WrongInputException.class)  public final void testInvalidFileName() {    Driver.execute("graph.txt", "invalid.txt");  }  @Test  public final void testEmptyFile() {    String actual = Driver.execute("graph.txt", "emptycommand.txt");    assertTrue("Nothing to output", actual.isEmpty());  }}